sys_script_client.script:
function onSubmit() {
    //Check to see if the form is for a new record.  
    if (g_form.isNewRecord()) {

        // Instantiate the GetLastOfficeLogNo Script Include 
        var getLogNo = new GlideAjax('GetLastOfficeLogNo');
        // Specify the getUserInfo method; GlideAjax uses sysparm_name to find which function to use
        getLogNo.addParam('sysparm_name', 'getLogNo');
        // Pass the form's parameters to the script include
        getLogNo.addParam('sysparm_office', g_form.getValue('u_office'));
        jslog("The current value of office on form is " + g_form.getValue('u_office'));
        // Send the request to the server
        getLogNo.getXML(populateUserFields);
    }

    // When the response is back from the server
    function populateUserFields(response) {
        // Extract the info from the response, clear any value from the field, 
        // set new value in the  field
        var infoFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('u_office_log_no');
        g_form.setValue('u_office_log_no', infoFromScriptInclude);
		// jslog("The current value of office number on form is "  + g_form.getValue('u_office_log_no'));

    }
}
