sys_script_client.script:
function onLoad() {
	var user_id = g_form.userID; //Get current user ID
	var ga = new GlideAjax('global.MTXClientUtil');
	ga.addParam('sysparm_name', 'getUserInfo');
	ga.addParam('sysparm_user', g_form.getValue(user_id));
	ga.getXML(processAnswer);
}
function processAnswer (response)  {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	var obj = JSON.parse(answer);
	g_form.setValue('u_department', obj.department);
	g_form.setValue('u_division', obj.u_division);
	g_form.setValue('u_office', obj.u_office);
}
