sys_script_include.script:
var MTXClientUtil = Class.create();
MTXClientUtil.prototype = Object.extendsObject(AbstractAjaxProcessor, {
	getUserInfo : function() {
		var usr = this.getParameter('sysparm_user');
		var obj = {};
		var gr = new GlideRecord('sys_user');
		gr.addQuery('sys_id', usr);
		gr.query();
		if(gr.next()) {
			obj.phone = gr.phone.toString();
			obj.email = gr.email.toString();
			obj.mobile_phone = gr.mobile_phone.toString();
			obj.department = gr.department.sys_id.toString();
			obj.manager = gr.manager.sys_id.toString();
			obj.u_division = gr.u_division.sys_id.toString();
			obj.u_office = gr.u_office.sys_id.toString();
		}
		var json = new JSON();
		var data = json.encode(obj);
		return data;
	},
	type: 'MTXClientUtil'
});
