sys_script_include.script:
var GetLastOfficeLogNo = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetLastOfficeLogNo.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
    // Define the GetLastOfficeLogNo function.  
    // Create a GlideRecord for the Log Number table.
    // Use the sysparm_office passed from the client side to retrieve a record from the Log Number table.
    // Return the requested values for the requested record
    getLogNo: function() {
        var grRec = new GlideRecord('x_522137_mailtrax_office_log_counters');
		grRec.get(this.getParameter('sysparm_office'));
        return grRec.counter;		
    },
    type: 'GetLastOfficeLogNo'
});
